mapper.insert:
INSERT INTO MEMBER(MEM_NO
				 , MEM_ID
				 , MEM_PASSWORD
				 , EMAIL
				 , MEM_NAME
				 , BIRTH
				 , GENDER
				 , PHONE
				 , MEM_LEVEL
				 , SIGNUP_DATE
				 , RESIGN_DATE)
VALUES ( MEMBER_SEQ.NEXTVAL
	   , #{memberId}
	   , #{memberPwd}
	   , #{email}
	   , #{memberName}
	   , #{birth}
	   , #{gender}
	   , #{phone}
	   , '회원'
	   , SYSDATE
	   , NULL			)

mapper.select:
SELECT COUNT(*) COUNT
	FROM MEMBER
WHERE MEM_ID = #{memberId}

mapper.update:
UPDATE MEMBER
	SET EMAIL = #{ email }
	  , BIRTH = #{ birth }
	  , PHONE = #{ phone } 		
WHERE MEM_ID = #{ memberId } AND RESIGN_DATE IS NULL

UPDATE MEMBER
	SET MEM_PASSWORD = #{ newPwd }
WHERE MEM_ID = #{ memberId } AND MEM_PASSWORD = #{ memberPwd } AND RESIGN_DATE IS NULL
